    def  startBtn(self):
        self.notification.setText("Enter your age :") 
        self.lineEdit.setText("")   
        

        
    def get_age(self):
        try:
            self.textlin = self.lineEdit.text()
            age = int(self.textlin)
            if age < 11:
                Answer = f"You are a {str(age)} years old child !"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")
            elif (age >= 12 and age <= 18 ):
                Answer = f"You are a {str(age)} years old teenager !"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")
            elif age == 19:
                Answer = f"You are of legal age and You are a {str(age)} years old teenager !"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")
            elif age == 20:
                Answer = f"You are a {str(age)} years old teenager !"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")
            elif (age >= 21 and age <= 34):
                Answer = f"You are a {str(age)} years old young !"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")
            elif (age >= 35 and age <= 49):
                Answer = f"You are a {str(age)} years old middle-aged !"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")
            elif (age >= 50 and age <= 121):
                Answer = f"You are a {str(age)} years old man or woman !"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")            
            else:
                Answer = f"Enter the correct age"
                self.Answer.setText(Answer)
                self.lineEdit.setText("")
        except ValueError:
                Answer = "Please enter only numbers in the entry ! "
                self.Answer.setText(Answer)
                self.lineEdit.setText("")

                

